import { createSlice } from "@reduxjs/toolkit";
import userSlice from "./userSlice.js";

const searchSlice = {
    name:"search",
    initiaState:{
        movieName:null,
        retmovie :null,
    },
    reducers:{
        setSearchMovie:(state,action)=>{
            const {search , movies}=action.playload
            state.movieName=search,
            state.retmovie=movies
        } 
    }
}

export const {setSearchMovie} =e.actions
export default searchSlice
