import React from "react";
import { CiPlay1 ,CiCircleInfo} from "react-icons/ci";
const VideoTitle =()=>{
    return(
        <div  className="absolute text-white mt-80 ml-8  ">
            <h3 className="font-bold text-3xl">HONG KONG</h3>
            <p>Contrary to popular belief, Lorem Ipsum is not simply random text.</p>
            
            <button className="flex bg-white text-gray-800 font-semibold p-1 px-2 rounded-md hover:opacity-80"><CiPlay1 size="24px" />
            <span className='ml-1'>Play</span></button>
            <button className="flex bg-white text-gray-800 font-semibold p-1 px-2 opacity-80 rounded-md ml-2 mt-2 hover:opacity-60"> <CiCircleInfo size="24px"/>
            <span className='ml-1'>Watch more</span> </button>
        </div>
    )
}
export default VideoTitle
