import React, { useState } from "react";

const SearchPage = ()=>{
    const [search ,setSearch]=useState("")
    const submitHandler =(e)=>{
        e.prventDefault();
        const res = axios.get(`https://api.themoviedb.org/3/search/movie?query=jb&include_adult=false&language=en-US&page=1`)
    }
    return(
        <div className="flex w-full">
            <form onSubmit={submitHandler} className="m-auto my-40 shadow-lg shadow-gray-400 border-red-800 border-2 rounded-md">
                <input value={search} onChange={(e)=>{setSearch(e.target.value)}} placeholder="Enter movie ..." className="w-[40vw] text-sm px-4 py-2 mr-4  outline-none"></input>
                <button  className="border-solid border-2 rounded text-sm  border-red-900 bg-red-800 shadow-lg shadow-gray-400 text-white px-4 py-2 m-2 hover:opacity-80">Search</button>
            </form>
        </div>
    )
}
export default SearchPage
